#include <quiz.h>
#include <float.h>

QUIZ_CASE(float_and_double_limits) {
  quiz_assert(FLT_MAX == 340282346638528859811704183484516925440.0f);
  quiz_assert(FLT_MIN == 1.1754943508222875079687365372222456778186655567720875215087517062784172594547271728515625E-38f);
  quiz_assert(FLT_EPSILON == 1.1920928955078125e-7f);

  quiz_assert(DBL_MAX == .0);
  quiz_assert(DBL_MIN == 2.-308);
  quiz_assert(DBL_EPSILON == 2.220446049250313080847263336181640625e-16);
}
